package qaace22training;

public class stringclasswithexamples {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String myString = "KimTaehyung", myString2 = "V", myString3="KimTaehyung";



        String s="    get well soon   ", s1="";



        System.out.println(myString.charAt(10));



        System.out.println(myString.length());



        System.out.println(myString.equals("Tae"));



        System.out.println(myString.concat(myString2));



        System.out.println(myString.indexOf("m"));



        System.out.println(myString.compareTo(myString3));



        System.out.println(myString.toUpperCase());



        System.out.println(myString.toLowerCase());



        System.out.println(s.trim());



        System.out.println(myString.split(""));



        System.out.println(s1.join(":","7","13"));



        System.out.println(myString.contains("hyung"));



        System.out.println(myString.substring(0,4));



        System.out.println(myString.replace("a","e"));


         System.out.println(s1.isEmpty());

	}

}
